    <form onSubmit={submitHandler}>
      <label>Add Color Name:</label>
      <input
        autoFocus
        type="text"
        placeholder="color name"
        required
        value={colorValue}
        onChange={(e) => setColorValue(e.target.value)}
      />
    </form>
  );
};

export default Input;
